mapper.select:
SELECT a.department as department, sum(total_cost) as totalCost, c.department as departmentName, b.all_cost as allCost FROM dms_child_project a left join dms_main_project b on a.parent_id = b.id left join dms_department c on c.id = a.department where a.confirm_status = 2
AND a.initial_delivery_date > #{startTime}
AND a.initial_delivery_date <= #{endTime}
GROUP BY a.department

select count(1) from ( SELECT a.department as department, sum(total_cost) as totalCost, c.department as departmentName, b.all_cost as allCost FROM dms_child_project a left join dms_main_project b on a.parent_id = b.id left join dms_department c on c.id = a.department where a.confirm_status = 2
AND a.initial_delivery_date > #{f.startTime}
AND a.initial_delivery_date <= #{f.endTime}
GROUP BY a.department ) as g

SELECT a.department as department, sum(total_cost) as totalCost, c.department as departmentName, b.all_cost as allCost FROM dms_child_project a left join dms_main_project b on a.parent_id = b.id left join dms_department c on c.id = a.department where a.confirm_status = 2
AND a.initial_delivery_date > #{f.startTime}
AND a.initial_delivery_date <= #{f.endTime}
GROUP BY a.department limit #{startPage},${pageRows}

SELECT a.user_id AS userId, a.designer AS designer, sum(a.total_cost) AS totalCost, sum(a.bonus) AS bonus, sum(a.fine) AS fine, sum(a.wages) AS wages, sum(a.designer_commission) AS designerCommission, sum(a.bonus) - sum(a.fine) + sum(a.wages) + sum(a.designer_commission) as payWages, sum(a.total_cost) - sum(a.bonus) + sum(a.fine) - sum(a.wages) - sum(a.designer_commission) AS profit FROM dms_child_project a WHERE a.confirm_status = 2
AND a.initial_delivery_date > #{startTime}
AND a.initial_delivery_date <= #{endTime}
AND a.department <= #{department}
GROUP BY a.user_id

select count(1) from (
SELECT
a.user_id AS userId
FROM
dms_child_project a
WHERE
a.confirm_status = 2

  AND a.initial_delivery_date > #{f.startTime}


  AND a.initial_delivery_date <= #{f.endTime}


  AND a.department <= #{f.department}

GROUP BY
a.user_id
) as c

SELECT a.user_id AS userId, a.designer AS designer, sum(a.total_cost) AS totalCost, sum(a.bonus) AS bonus, sum(a.fine) AS fine, sum(a.wages) AS wages, sum(a.designer_commission) AS designerCommission, sum(a.bonus) - sum(a.fine) + sum(a.wages) + sum(a.designer_commission) as payWages, sum(a.total_cost) - sum(a.bonus) + sum(a.fine) - sum(a.wages) - sum(a.designer_commission) AS profit FROM dms_child_project a WHERE a.confirm_status = 2
AND a.initial_delivery_date > #{f.startTime}
AND a.initial_delivery_date <= #{f.endTime}
AND a.department <= #{f.department}
GROUP BY a.user_id limit #{startPage},${pageRows}
