mapper.select:
select b.project_creation_time as projectCode, b.customer_source as customerSource, b.customer_code as customerCode, b.place as place, b.sales_area as salesArea, b.cost_price as costPrice, b.design_sales_cost as designSalesCost, a.parent_id as parentId, a.project_type_code as typeCode, a.project_type_name as typeName, a.construction_area as constructionArea, a.cost_price as childCostPrice, a.total_cost as totalCost, a.total_sales_price as totalSalesPrice, a.designer_commission_price as designerCommissionPrice, a.designer_commission as designerCommission, a.department as department, a.designer as designer from dms_child_project a left join dms_main_project b on a.parent_id = b.id where 1=1 and a.confirm_status = 2
and a.project_type_code = #{f.type}
and a.confirm_time >= #{f.startTime}
and a.confirm_time <= #{f.endTime}
order by a.id desc limit #{startPage},${pageRows}

select count(1)
from dms_child_project a left join dms_main_project b on a.parent_id = b.id
where 1=1 and a.confirm_status = 2

  and a.project_type_code = #{f.type}


    and a.department = #{f.department}


  and a.confirm_time >= #{f.startTime}


  and a.confirm_time <= #{f.endTime}

select b.project_creation_time as projectCode, b.customer_source as customerSource, b.customer_code as customerCode, b.place as place, b.sales_area as salesArea, b.cost_price as costPrice, b.design_sales_cost as designSalesCost, a.parent_id as parentId, a.project_type_code as typeCode, a.project_type_name as typeName, a.acquisition_date as acquisitionDate, a.initial_delivery_date as initialDeliveryDate, a.construction_area as constructionArea, a.cost_price as childCostPrice, a.total_cost as totalCost, a.designer_commission_price as designerCommissionPrice, a.designer_commission as designerCommission, a.department as department, a.designer as designer from dms_child_project a left join dms_main_project b on a.parent_id = b.id where 1=1 and a.confirm_status = 2
and a.project_type_code = #{type}
and a.department = #{department}
and a.confirm_time >= #{startTime}
and a.confirm_time <= #{endTime}
